<script>
    // these are helper functions to set and erase cookies
    function setCookie(name, value, days) {
      var expires = "";
      if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
        expires = "; expires=" + date.toUTCString();
      }
      document.cookie = name + "=" + (value || "") + expires + "; path=/";
    }

    function getCookie(name) {
      var nameEQ = name + "=";
      var ca = document.cookie.split(';');
      for (var i = 0; i < ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ') c = c.substring(1, c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
      }
      return null;
    }
    function eraseCookie(name) {
      document.cookie = name + '=; Max-Age=-99999999;';
    }


    //this our actual cookie handler
    function myChFunction(cookieTypes) {
      console.log("cookie handler function has been called for:", cookieTypes);

      if (cookieTypes.application) {
        // set required cookies here
        setCookie("myRequiredCookie","this is a required cookie");
      } else {
        // remove required cookie here
        eraseCookie("myRequiredCookie");
      };

      if (cookieTypes.usage) {
        // set user personalization cookies here
        setCookie("usage1","green");
        setCookie("usage2","blue");
      } else {
        // remove user personalization cookies here
        eraseCookie("usage1");
        eraseCookie("usage2");
      };

      if (cookieTypes.statistics) {
        // set site statistics gathering cookies here
        setCookie("statCookie","counter");
      } else {
        // remove site statistics gathering cookies here
        eraseCookie("statCookie");
      };

      if (cookieTypes.advertising) {
        // set advertising and marketing and tracking cookies here
        setCookie("marketingCookie","ads for all");
      } else {        
        // remove advertising and marketing and tracking cookies here
        eraseCookie("marketingCookie");
      };      
    }



</script>

